import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib"
import React, { ReactNode, useState } from "react"
import { v4 as uuidv4 } from 'uuid'
import Graph from 'react-graph-vis';


class StreamlitVisGraph extends StreamlitComponentBase {

  public render = (): ReactNode => {

    function lookup_node_id(lookup_node: any, mynodes: any){
      for (let node of mynodes){
          if (node.id === lookup_node){
              return node;
          }
    }}

    var nodes = JSON.parse(this.props.args["nodes"]);
    var edges = JSON.parse(this.props.args["edges"]);
    var graph = {
      "nodes": nodes,
      "edges": edges
    }
    // var graph = {
    //   nodes: [
    //     { id: 1, label: "Node 1", title: "node 1 tootip text" },
    //     { id: 2, label: "Node 2", title: "node 2 tootip text" },
    //     { id: 3, label: "Node 3", title: "node 3 tootip text" },
    //     { id: 4, label: "Node 4", title: "node 4 tootip text" },
    //     { id: 5, label: "Node 5", title: "node 5 tootip text" }
    //   ],
    //   edges: [
    //     { from: 1, to: 2 },
    //     { from: 1, to: 3 },
    //     { from: 2, to: 4 },
    //     { from: 2, to: 5 }
    //   ]
    // };

    var nodes = graph.nodes.slice();

    // for (let i = 0; i < nodes.length; i++) {
    //   if(nodes[i].title)
    //     nodes[i].div = this.htmlTitle(nodes[i].title);
    // }

    // const options = JSON.parse(this.props.args["config"]);
    const options = {
      layout: {
        hierarchical: true
      },
      edges: {
        color: "#000000"
      },
      height: "500px"
    };


    const events = {

      selectNode: (event:any) => {
        console.log(event.nodes[0])
        Streamlit.setComponentValue(event.nodes[0]);
      },

      doubleClick: (event:any) => {
        console.log(event.nodes);
        // let link = nodes;
        let lookup_node = lookup_node_id(event.nodes[0], nodes);
        Streamlit.setComponentValue(lookup_node)
        // let link = lookup_node.div.innerHTML;
        // if(link){
        //   window.open(link);
        // }
      }
    };
    return (
      <span>

      <Graph
      graph={graph}
      options={options}
      events={events}
      key={uuidv4()}
      getNetwork={(network: any) => {
        //  if you want access to vis.js network api you can set the state in a parent component using this property
        //console.log(network);
      }}/>
      </span>
    )
  }

  private htmlTitle = (html: string):any => {   
    const container = document.createElement("div");
    container.innerHTML = html;
    return container;
  }
}

export default withStreamlitConnection(StreamlitVisGraph)
