import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib"
import React from "react"
import Graph from 'react-graph-vis';

// const options = {
//   layout: {
//     hierarchical: false
//   },
//   edges: {
//     color: "#000000"
//   }
// };

interface State{
  graph: any,
  events: any,
  options: any
}

class StreamlitVisGraph extends StreamlitComponentBase<State> {
  public state = {
    graph: JSON.parse(this.props.args["data"]),
    events: {
      select: (event: any) => {
        console.log("Selected nodes:");
        console.log(event.nodes[0]);
        Streamlit.setComponentValue(event.nodes[0])
      },
      // doubleClick: ({ pointer: { canvas } }) => {
      //   createNode(canvas.x, canvas.y);
      // }
    },
    options: JSON.parse(this.props.args["options"])
  }

  render = () => {
    const { theme } = this.props
    const style: React.CSSProperties = {}
    return (
        <Graph graph={this.state.graph} options={this.state.options} events={this.state.events} style={style} />
    );
  }
}

export default withStreamlitConnection(StreamlitVisGraph)
