import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib"
import React from "react"
import Graph from 'react-graph-vis';
import { networkInterfaces } from "os";

// const options = {
//   layout: {
//     hierarchical: false
//   },
//   edges: {
//     color: "#000000"
//   }
// };

interface State{
  graph: any,
  events: any,
  options: any,
  network: any
}

class StreamlitVisGraph extends StreamlitComponentBase<State> {
  public state = {
    graph: JSON.parse(this.props.args["data"]),
    events: {
      select: (event: any) => {
        console.log("Selected nodes:");
        console.log(event.nodes[0]);
        Streamlit.setComponentValue(event.nodes[0])
      },
      // doubleClick: ({ pointer: { canvas } }) => {
      //   createNode(canvas.x, canvas.y);
      // }
    },
    options: JSON.parse(this.props.args["options"]),
    network: {}
  }

  render = () => {
    return (
      <span style={{height:'100%',width:'100%'}}>
        <Graph
        graph={this.state.graph}
        options={this.state.options}
        events={this.state.events}
        getNetwork={(network: any) => {
          network.fit()
          network.resize()
          //  if you want access to vis.js network api you can set the state in a parent component using this property
          //console.log(network);
        }}
        />
      </span>
    );
  }
}

export default withStreamlitConnection(StreamlitVisGraph)
